import React from 'react';
import {
  Avatar,
  Box,
  Button,
  Container,
  Grid,
  makeStyles,
  TextField,
  Typography,
  Icon,
  List,
  ListSubheader
} from '@material-ui/core';
import { loadCSS } from 'fg-loadcss';
import Typist from 'react-typist';
import Fade from 'react-reveal/Fade';

import '../App.css'

const useStyles = makeStyles((theme) => ({
  root: {
    '& > .fa': {
      margin: theme.spacing(2),
    },
  },
  button: {
      boxShadow: '0px 4px 32px rgba(255, 255, 255, 0.4)',
      background:'black',
      borderRadius: 100,
      color:'white',
      width:200,
      marginBottom: theme.spacing(3),
      whiteSpace:'nowrap'
    },
}));


function Homepage ({ match }) {
  const [titleDone, setTitleDone] = React.useState(false)
  const styles = useStyles()
  function handleTitleDone() {
    setTitleDone(true)
  }
  React.useEffect(() => {
    loadCSS(
      'https://use.fontawesome.com/releases/v5.1.0/css/all.css',
      document.querySelector('#font-awesome-css'),
    );
  }, []);
  function body() {
    return (
      <Fade>
            <p className='body'> I'm a full-time first year Computer science student and a part-time Full-Stack developer.</p>
            <Grid
             container
              direction="row"
              justify="center"
              alignItems="center"
              spacing={6}
            >
            <Button size='medium' className={styles.button} style={{marginTop:50}}> View my work </Button>
            </Grid>
      </Fade>
    )
  }
  function toFacebook() {
    window.location.href = "http://www.facebook.com/hoya.lee0";
  }
  function toInstagram() {
    window.location.href = "http://www.instagram.com/hxyalee";    
  }
  function toGithub() {
    window.location.href = "https://github.com/hxyalee";    
  }
  function toLinkedin() {
    window.location.href = "https://www.linkedin.com/in/hoya-lee/";    
  }  
  return(
    <div>
      <div>
        <Typist 
          cursor={{show:false}}
          onTypingDone={handleTitleDone}
        >
          <p className='name'> Hello, I'm Hoya.</p> 
        </Typist>
        {
          titleDone ?         
         body():null
        }

    </div> 
    <div className={styles.root}>
      <Grid
      container
      direction="row"
      justify="center"
      alignItems="flex-end"
      spacing={10}
      >
        <div style={{marginBottom:50, position:'absolute', bottom:'0'}}>
          <Button onClick={toFacebook}>
            <Icon className="fab fa-facebook"  style={{fontSize:50, margin:20}}/>
          </Button>
          <Button onClick={toInstagram}>
            <Icon className="fab fa-instagram" style={{fontSize:50, margin:20}}/>
          </Button>
          <Button onClick={toGithub}>
            <Icon className="fab fa-github" style={{fontSize:50, margin:20}}/>
          </Button>
          <Button onClick={toLinkedin}>
            <Icon className="fab fa-linkedin" style={{fontSize:50, margin:20}}/>
          </Button>

        </div>
      </Grid>
    </div>
  </div>
  )
}
export default Homepage
