import React from 'react';
import {
  Avatar,
  Box,
  Button,
  Container,
  Grid,
  makeStyles,
  TextField,
  Typography,
  List,
  ListSubheader
} from '@material-ui/core';
import Typist from 'react-typist';
import Fade from 'react-reveal/Fade';

import '../App.css'

const useStyles = makeStyles((theme) => ({
  card: {
  minWidth: 100,
  margin: 40,
  marginBottom:50

  },

  title: {
  fontSize: 14,
  },
  description: {
  margin:10
  },
  appBar: {
  position: 'relative',
  },
  fabButton: {
  align:'right' 
  },
  button: {
      boxShadow: '0px 4px 32px rgba(255, 255, 255, 0.4)',
      background:'black',
      borderRadius: 100,
      color:'white',
      width:200,
      marginBottom: theme.spacing(3),
      whiteSpace:'nowrap'
    },
}));


function Homepage ({ match }) {
  const [titleDone, setTitleDone] = React.useState(false)
  function handleTitleDone() {
    setTitleDone(true)
  }
  function body() {
    return (
      <Fade>
            <p className='body'> I'm a Full-Stack developer.</p>
            <Button className={'button'}> View my work </Button>
      </Fade>
    )
  }
  return(
    <div>
      <div>
        <Typist 
          cursor={{show:false}}
          onTypingDone={handleTitleDone}
        >
          <p className='name'> Hello, I'm Hoya.</p> 
        </Typist>
        {
          titleDone ?         
         body():null
        }

    </div>      
  </div>
  )
}
export default Homepage
