import React from 'react';
import {
  Avatar,
  Box,
  Button,
  Container,
  Grid,
  makeStyles,
  TextField,
  Typography,
  Icon,
  List,
  ListSubheader
} from '@material-ui/core';
import Typist from 'react-typist';
import Fade from 'react-reveal/Fade';

import '../App.css'

const useStyles = makeStyles((theme) => ({
  root: {
    '& > .fa': {
      margin: theme.spacing(2),
    },
  },
  button: {
      boxShadow: '0px 4px 32px rgba(255, 255, 255, 0.4)',
      background:'black',
      borderRadius: 100,
      color:'white',
      width:200,
      marginBottom: theme.spacing(3),
      whiteSpace:'nowrap'
    },
}));


function Homepage ({ match }) {
  const [titleDone, setTitleDone] = React.useState(false)
  const styles = useStyles()
  function handleTitleDone() {
    setTitleDone(true)
  }
  function body() {
    return (
      <Fade>
            <p className='body'> I'm a full-time first year Computer science student and a part-time Full-Stack developer.</p>
            <Grid
             container
              direction="row"
              justify="center"
              alignItems="center"
              spacing={6}
            >
            <Button size='medium' className={styles.button} style={{marginTop:50}}> View my work </Button>
            </Grid>
      </Fade>
    )
  }

  return(
    <div>
      <div>
        <Typist 
          cursor={{show:false}}
          onTypingDone={handleTitleDone}
        >
          <p className='name'> Hello, I'm Hoya.</p> 
    <Icon className="fa fa-plus-circle" style={{color:black[500]}}/>
        </Typist>
        {
          titleDone ?         
         body():null
        }

    </div> 
  </div>
  )
}
export default Homepage
